let timerRunning = false;
let startTime = undefined;
const startUserTimer = () => {
  let endTime;
  const winpointsBTN = document.getElementById('increaseWinpointsBTN');
  winpointsBTN.addEventListener("click", (event) => {
    startTime = new Date().now;
    console.log(event);
    endTime = startTime + 900000;
    timerRunning = true;
    winpointsBTN.style.display = "none";
  });
};
setInterval(function() {
  const timer_clock = document.getElementById('timer_clock');
  var delta = Date.now() - startTime; // milliseconds elapsed since start
  // alternatively just show wall clock time:

  timer_clock.innerHTML = `Time until respawn: ${Math.floor(delta / 1000)}`;

}, 1000);

export { startUserTimer }
