const updateUserPoints = () => {
  const userPoints = document.querySelector('userpoints');
  const winpointsBTN = document.getElementById('increaseWinpointsBTN');
  winpointsBTN.addEventListener("click", (event) => {
    userPoints.innerHTML = userPoints.innerHTML + 10;
  });
};

export { updateUserPoints }
