const startUserTimer = () => {
  const winpointsBTN = document.getElementById('increaseWinpointsBTN');
  winpointsBTN.addEventListener("click", () => {
    localStorage.setItem("endTime", (new Date().getTime() + 900000));
    localStorage.setItem("timerRunning", true);
    winpointsBTN.style.display = "none";
  });
};


setInterval(function() {
  if (localStorage.getItem("timerRunning")) {
    const timer_clock = document.getElementById('timer_clock');
    var now = new Date().getTime();
    var delta = parseInt(localStorage.getItem("endTime")) - now; // milliseconds elapsed since start
    var minutes = Math.floor((delta % (1000 * 60 * 60)) / (1000 * 60));
    var seconds = Math.floor((delta % (1000 * 60)) / 1000);
    timer_clock.innerHTML = `Time until respawn: ${minutes}:${seconds}`;
  }
  if (delta <= 0) {
    stopUserTimer();
  }
}, 1000);

const stopUserTimer = () => {
  const winpointsBTN = document.getElementById('increaseWinpointsBTN');
  localStorage.removeItem("endTime");
  localStorage.removeItem("timerRunning");
};
export { startUserTimer }
