const startUserTimer = setInterval(function() {
  let endTime;
  const timerDiv = document.querySelector('timer');
  const winpointsBTN = document.getElementById('increaseWinpointsBTN');
  winpointsBTN.addEventListener("click", (event) => {
    var startTime = new Date().now;
    console.log(event);
    endTime = startTime + 900000;
    winpointsBTN.style.display = "none";
    timerDiv.insertAdjacentHTML("afterbegin", `<h5>Time until respawn: ${endTime-startTime} </h5>`)
  });
}, 1000);

export { startUserTimer }
